
public class Dotoperatorpg {

	
		
			void show() {
		        int i = 67;
		        System.out.println("In show method: "+ i);
		    }

		    static boolean isGreater(int a, int b) {
		        return a > b;
		    }
			public static void main(String[] args) {
				// TODO Auto-generated method stub
				Dotoperatorpg doe = new Dotoperatorpg();
		        doe.show();
		        System.out.println("Is 7>4: " + Dotoperatorpg.isGreater(7, 4));
			}

		

	}


